"use strict";

module.exports = {
  ensureUnauthUser: function ensureUnauthUser(req, res, next) {
    if (req.isAuthenticated()) {
      return next();
    } else {
      res.redirect('/');
    }
  },
  ensureMainUser: function ensureMainUser(req, res, next) {
    if (req.isAuthenticated()) {
      res.redirect('/dashboard');
    } else {
      return next();
    }
  }
};
