sourcesContent:
module.exports = {
    ensureUnauthUser: function(req, res, next){
        if(req.isAuthenticated()){
            return next();
        } else {
            res.redirect('/');
        }
    },
    ensureMainUser: function(req, res, next){
        if(req.isAuthenticated()){
            res.redirect('/dashboard');
        } else {
            return next();
        }
    }
};
